package http

import (
	entity "bili_danmaku/internal/types"
	"encoding/base64"
	"encoding/json"
	"github.com/avast/retry-go/v4"
	"github.com/go-resty/resty/v2"
	"github.com/zeromicro/go-zero/core/logx"
	"time"
)

func GetUserInfo() (userinfo *entity.UserinfoLite) {
	var resp *resty.Response
	userinfo = new(entity.UserinfoLite)
	var url = "https://api.bilibili.com/x/web-interface/nav"
	var err error
	r := &entity.UserInfo{}
	err = retry.Do(func() error {
		if resp, err = cli.R().
			SetHeader("user-agent", userAgent).
			SetHeader("cookie", CookieStr).
			Get(url); err != nil {
			return err
		}
		logx.Debug(string(resp.Body()))
		if err = json.Unmarshal(resp.Body(), r); err != nil {
			logx.Error("Unmarshal失败：", err, "body:", string(resp.Body()))
			return err
		}
		return nil
	}, retry.Attempts(3), retry.Delay(1*time.Second))
	if err != nil {
		logx.Error(err)
		return
	}
	if r.Code == -101 {
		logx.Info("用户未登录")
		return userinfo
	}
	if resp, err = cli.R().
		SetHeader("user-agent", userAgent).
		SetHeader("cookie", CookieStr).
		Get(r.Data.Face); err != nil {
		userinfo.Avactor = "data:image/gif;base64,"
	}
	userinfo.Avactor = "data:image/gif;base64," + base64.StdEncoding.EncodeToString(resp.Body())
	userinfo.Islogin = true
	userinfo.Username = r.Data.Uname
	userinfo.Uid = r.Data.Mid
	return userinfo
}
